package com.sci;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int	i = 10;
		if (i < 10) {
			System.out.println(i + "i is smaller than 10");
		}else if (i > 10) {
			System.out.println(i + "i is grater than 10");
		}else {
			System.out.println(i + "i is  10");
		}
	}

}
